mapper.insert:

	SELECT IFNULL(MAX(qna_number), 0) -- qna_number 최대값 조회(null 일 경우 기본값 0 사용)
	FROM qna


INSERT
INTO qna
VALUES (
	#{qna_number} + 1    -- selectKey 를 통해 조회된 게시물 번호 + 1 값 사용
	, #{qna_type_idx}    -- qna_type_idx(1 = 예약/계약상담)
	, #{qna_inquery}
	, #{qna_content}
	, null
	, now()
	,#{mem_id}
)

mapper.update:


	SELECT bo_readcount
	FROM board_notice
	WHERE bo_idx = #{bo_idx}

UPDATE board_notice
SET bo_readcount = bo_readcount + 1
WHERE bo_idx = #{bo_idx}
