mapper.insert:
		INSERT
		INTO member_info
		VALUES (
			null
			,#{mem_id}
			,#{mem_passwd}
			, CONCAT(#{mem_email1}, '@', #{mem_email2})
			,#{mem_post_code}
			,#{mem_add1}
			,#{mem_add2}
			,#{mem_name}
			,#{mem_jumin}
			,#{mem_tel}
			, 1                   -- sns수신
			,#{mem_birthday}
			, now()               -- 가입일자
			, null                -- 탈퇴일자
			, 1                   -- 회원상태
			, 'N'                 -- 메일인증여부

		)

mapper.select:
SELECT *
FROM member_info
WHERE 
	mem_tel = #{mem_tel}
	
		AND mem_id = #{mem_id}

mapper.update:
		UPDATE member_info
		SET 
			mem_passwd = #{mem_passwd}
		WHERE
			mem_tel = #{mem_tel}
