mapper.select:
SELECT * FROM car_model_info WHERE 1=1 -- car_typeList가 있을 경우 AND car_type IN
#{car_type}
ORDER BY car_model

		SELECT *
		FROM car_info
		WHERE brc_idx = #{brc_idx}
	        	-- car_fuel_typeList가 있을 경우
	            AND car_fuel_type IN
	            
	                #{car_fuel_type}
	            
	        
	        	-- car_age가 있을 경우
	            
	                
		                	-- 만 26세 이상
		                	AND car_age >= #{car_age}
		                
		                	-- 만 21 ~ 25세
		                	AND car_age BETWEEN 21 AND 25
		                
	                
	            
	        
	        	-- car_careerList가 있을 경우
	            
	                
		                	-- 3년 이상
		                	AND car_career >= #{car_career}
		                
		                	-- 1년~3년 미만
		                	AND car_career BETWEEN 1 AND 2
		                
	                
	            
	        
	         -- car_oldList가 있을 경우
                AND (
			        car_old BETWEEN #{car_opts.car_oldList.startYear} AND #{car_opts.car_oldList.endYear}
			    )
            
	        	-- pet_opt가 있을 경우
	            
	               	AND pet_opt = #{pet_opt}
	            
	        
            
		
		
		ORDER BY car_model

SELECT car_model_image
FROM car_model_info
WHERE car_model = #{car_model}
